userDefinedLinkHintCss:
/* click flash */
div.vimiumFlash,
div.vimium-flash {
    box-shadow: none;
    outline: 3px solid rgba(0, 0, 0, 0.5);
    outline-offset: 4px;
}

/* linkhint boxes */
#vimiumHintMarkerContainer div.internalVimiumHintMarker, #vimiumHintMarkerContainer div.vimiumHintMarker {
    background: #fff;
    border-radius: 0;
    border: 2px solid #000;
    color: black;
    font-weight: bold;
    font-size: 12px;
}

div > .vimiumHintMarker {
    background: #fff;
    border-radius: 0;
    border: 2px solid #000;
}

/* linkhint text */
div > .vimiumHintMarker span {
    color: black;
    font-weight: bold;
    font-size: 12px;
}

.vimiumReset,
div.vimiumReset,
span.vimiumReset,
table.vimiumReset,
a.vimiumReset,
a:visited.vimiumReset,
a:link.vimiumReset,
a:hover.vimiumReset,
td.vimiumReset,
tr.vimiumReset {
    font-family: sans-serif;
}


#vomnibar {
    border-radius: 0;
    border: 0;
    box-shadow: 0 2px 16px rgba(0,0,0,0.3);
    color: #2d2d2d;
    left: 50%;
    margin-left: -320px;
    max-width: 640px;
    top: 18px;
}

#vomnibar .vomnibarSearchArea {
    background: #F9FAF9;
    border: 0;
    border-radius: 0;
    padding: 0;
}

#vomnibar input {
    border-radius: 0;
    border: 0;
    box-shadow: none;
    font-size: 24px;
    height: initial;
    padding: 16px 24px;
}

#vomnibar ul {
    background: inherit;
    border-radius: 0;
    padding: 0;
    margin: 0;
}

#vomnibar li {
    border: 0;
    color: #2d2d2d;
    display: flex;
    line-height: 1;
}

#vomnibar li .vomnibarTopHalf,
#vomnibar li .vomnibarBottomHalf {
    font-size: 13px;
    margin: 0;
    padding: 0;
}

#vomnibar li .vomnibarTopHalf {
    display: flex;
    flex: 0 0 67%;
    margin-right: 16px;
}

#vomnibar li .vomnibarBottomHalf {
    flex: 0 0 33%;
    overflow: hidden;
    text-overflow: ellipsis;
    color: transparent;
}

#vomnibar li .vomnibarSource.vomnibarNoInsertText,
#vomnibar li .vomnibarSource.vomnibarInsertText {
    display: none;
}

#vomnibar li .vomnibarSource,
#vomnibar li .vomnibarUrl {
    color: #979797;
}

#vomnibar li .vomnibarSource {
    display: block;
    flex-shrink: 0;
    font-size: 13px;
    line-height: 1.6;
    margin-right: 12px;
    text-transform: uppercase;
}

#vomnibar li .vomnibarTitle,
#vomnibar li .vomnibarUrl {
    line-height: 1.6;
}

#vomnibar li .vomnibarTitle {
    color: #2d2d2d;
    display: block;
    margin: 0;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

#vomnibar li .vomnibarMatch {
    color: inherit;
}

#vomnibar li.vomnibarSelected {
    background: #222222;
}

#vomnibar li.vomnibarSelected .vomnibarSource,
#vomnibar li.vomnibarSelected .vomnibarUrl {
    color: #ebebeb;
}

#vomnibar li.vomnibarSelected .vomnibarTitle {
    color: #f4f4f4;
}

#vomnibar li.vomnibarSelected .vomnibarMatch {
    color: inherit;
}

div.vimiumHUD {
    border-radius: 0;
    box-shadow: unset;
    left: unset;
    right: 0;
}
